\begin{align}
\mathrm{similarity}(\mathbf{x}, \mathbf{y})
   &= \frac{
          \mathbf{x} \cdot \mathbf{y}
      }{
          \|\mathbf{x}\| \|\mathbf{y}\|
      }
\\
   &= \frac{
          x_1 y_1 + x_2 y_2 + \cdots + x_n y_n
      }{
          \sqrt{x_1^2 + x_2^2 + \cdots + x_n^2}
          \sqrt{y_1^2 + y_2^2 + \cdots + y_n^2}
      }
\\
   &= \frac{
          \sum_{n} x_n y_n
      }{
          \sqrt{\sum_{n} x_n^2}
          \sqrt{\sum_{n} y_n^2}
      }
\end{align}

# コサイン類似度の実装
def cos_similarity(x, y, eps=1e-8):
    # コサイン類似度を計算:式(2.1)
    nx = x / (np.sqrt(np.sum(x**2)) + eps)
    ny = y / (np.sqrt(np.sum(y**2)) + eps)
    return np.dot(nx, ny)

\mathrm{PMI}(x, y)
    = \log_2 \frac{
          P(x, y)
      }{
          P(x) P(y)
      }

\begin{align}
\mathrm{PMI}(x, y)
   &= \log_2 \left(
          P(x, y)
          \frac{1}{P(x)}
          \frac{1}{P(y)}
      \right)
\\
   &= \log_2 \left( 
          \frac{C(x, y)}{N}
          \frac{N}{C(x)}
          \frac{N}{C(y)}
      \right)
\\
   &= \log_2 \frac{
          C(x, y) N
      }{
          C(x) C(y)
      }
\end{align}

